#!/bin/sh
gcloud beta container --project "iarpa-microns-seunglab" clusters create "$1" --zone "us-central1-a" --username "admin" --cluster-version "1.11.5-gke.5" --machine-type "f1-micro" --image-type "COS" --disk-type "pd-standard" --disk-size "100" --scopes "https://www.googleapis.com/auth/devstorage.read_only","https://www.googleapis.com/auth/logging.write","https://www.googleapis.com/auth/monitoring","https://www.googleapis.com/auth/servicecontrol","https://www.googleapis.com/auth/service.management.readonly","https://www.googleapis.com/auth/trace.append" --num-nodes "3" --enable-cloud-logging --enable-cloud-monitoring --enable-ip-alias --network "projects/iarpa-microns-seunglab/global/networks/seamless" --subnetwork "projects/iarpa-microns-seunglab/regions/us-central1/subnetworks/seamless-subnet" --default-max-pods-per-node "110" --addons HorizontalPodAutoscaling,HttpLoadBalancing --enable-autoupgrade --enable-autorepair && gcloud beta container --project "iarpa-microns-seunglab" node-pools create "gpu-pool-1" --cluster "$1" --zone "us-central1-a" --node-version "1.11.5-gke.5" --machine-type "n1-standard-1" --accelerator "type=nvidia-tesla-k80,count=1" --image-type "COS" --disk-type "pd-standard" --disk-size "16" --scopes "https://www.googleapis.com/auth/cloud-platform" --preemptible --num-nodes "$2" --enable-autoupgrade --enable-autorepair